import React from 'react';
import { MemoryRouter, Switch, Route } from 'react-router-dom';

import Jumbotron from 'react-bootstrap/Jumbotron';
// import Container from 'react-bootstrap/Container';
import Container from '@material-ui/core/Container';
import Image from 'react-bootstrap/Image';
import profile from './img/profile_picture.JPG'

import NavigationBar from './NavigationBar';
import Projects from './Projects';
import Home from './Home';
import Experience from './Experience';

import './App.css';
import { Typography } from '@material-ui/core';

const root = {
  backgroundColor: '#ecf0f1',
}

const InnerContainerStyles = {
  color: 'black',
  marginTop: '5em',
}

const JumbotronStyles = {
  background: 'transparent',
  minHeight: '675px',
  margin: 0,
  marginTop: '3em',
}

const JumbotronStylesSecondary = {
  background: 'transparent',
  minHeight: '600px',
  margin: 0,
  padding: 0
}


const pictureSizing = {
  maxHeight: '320px',
  maxWidth: '320px',
  overflow: 'hidden',
}

function App() {
  const [clicked, setClick] = React.useState(false)
  const handleClick = () => setClick(true);
  console.log(clicked);

  return(
    <MemoryRouter>
      <div style={root}>
        <NavigationBar handleClick={handleClick} />
        <Jumbotron fluid style={JumbotronStyles}>
          <Container style={InnerContainerStyles} className="w-50 mt-20">
            <h1 className="header">
              <Container style={pictureSizing}>
                <Image src={profile} roundedCircle fluid/>
              </Container>
            </h1>
            <h1 className="header header-hi">
              <div className="animate__animated animate__fadeInDown">
                <Typography variant="h2">
                  Hi, I'm Reda Herradi
                </Typography>
              </div>
            </h1>
          </Container>
        </Jumbotron>
        {
          clicked ?
          <Jumbotron style={JumbotronStylesSecondary}>
            <Container style={InnerContainerStyles}>
                    <Switch>
                      <Route path="/about">
                        <Projects />
                      </Route>
                      <Route path="/experience">
                        <Experience />
                      </Route>
                      <Route path="/">
                        <Home />
                      </Route>
                    </Switch>
              </Container>
          </Jumbotron>
          :
          null
        }
      </div>
    </MemoryRouter>
  );
}

export default App;
