import React from 'react';
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Image from 'react-bootstrap/Image';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from '@material-ui/core/Button';

import plant from './img/plant_over.jpg';
import items from './items/items.json';

function Experience(){
    let rootStyles = {
        marginBottom: '2.5em',
        "--animate-duration": "2000ms",
        "--animate-delay": "1.3s"
    }
    const lastRootStyles = {
        marginBottom: '0',
        "--animate-duration": "2000ms",
        "--animate-delay": "1.3s"
    }
    const contentStyles = {
        flex: '0 1 auto',
    }
    const textStyles = {
        marginTop: '1em',
    }
    const pictureSizing = {
        maxWidth: '100%',
        maxHeight: '100%',
        overflow: 'hidden',
    }

    const experiences = items.experience;

    return(
        <Container>
            {experiences.map((item, index) => {
                if(index === experiences.length - 1)
                    rootStyles = lastRootStyles;
                
                return(
                    <Card className="animate__animated animate__fadeInUp" style={rootStyles} key={item.id}>
                        <Row>
                            <Col xs={6} md={4} style={pictureSizing}>
                                <Image src={plant} fluid/>
                            </Col>
                            <Col>
                                <CardContent style={contentStyles}>
                                        <Typography component="h5" variant="h5">
                                            {item.title}
                                        </Typography>
                                        <Typography component="sub" variant="subtitle2">
                                            {item.dates}
                                        </Typography>
                                    
                                        <p className="lead" style={textStyles}>
                                            {item.desc}
                                        </p>
                                        <Button size="small" color="primary" href={item.link}>
                                            LINK
                                        </Button>
                                </CardContent>
                            </Col>
                        </Row>
                    </Card>
                );
            })}
        </Container>
    );
}

export default Experience;
